import React, { useState } from "react";
import ReactPlayer from "react-player/youtube";
import "./App.css";
import Header from "./components/Header";
import SearchBar from "./components/SearchBar";
import VideoList from "./components/VideoList";

const dummyVideos = [
  { title: "title 1", vid: "x7VfLpDIDN4" },
  { title: "title 2", vid: "PqiddY3o3aY" },
  { title: "title 3", vid: "a-cw5ybQ8ps" },
  { title: "title 4", vid: "ECZp_xpyxN0" },
  { title: "title 5", vid: "zWPsjhBaRb0" },
];

function App() {
  const [searchTitle, setSearchTitle] = useState("");

  const onSubmitHandler = (title) => {
    setSearchTitle(title);
  };
  const clickTitleHandler = (vide) => {};
  return (
    <div className="App">
      <Header />
      <SearchBar onSubmit={onSubmitHandler} />
      <div className="content">
        <div className="searched-video">
          <ReactPlayer
            className="video-player"
            width="100%"
            height="50rem"
            controls
            url="https://youtu.be/x7VfLpDIDN4"
          />
        </div>
        <div className="searched-videos">
          <VideoList keyword={searchTitle} onClickTitle={clickTitleHandler} />
        </div>
      </div>
    </div>
  );
}

export default App;
