import React, { useState } from "react";
import "./SearchBar.css";

// Search url=https://youtube.googleapis.com/youtube/v3/search?part=snippet&maxResults=25&q=ramayan&key=[YOUR_API_KEY]
const APIkey = "AIzaSyAtTWostq1MsBKVBHh6HeQ2bX6y6xj8YyE";

export default function SearchBar(props) {
  const [searchTitle, setSearchTitle] = useState("");
  const [searchedVideos, setSearchedVideos] = useState([]);

  const onChangeHandler = (event) => {
    setSearchTitle(event.target.value);
  };

  const searchVideos = (url) => {
    fetch(url)
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        const videos = data.items.map((item) => {
          return {
            id: item.id.videoId,
            vid: item.id.videoId,
            title: item.snippet.title,
            publishedAt: item.snippet.publishedAt,
            channelTitle: item.snippet.channelTitle,
            description: item.snippet.description,
          };
        });
        setSearchedVideos(videos);
      });
  };

  const onSubmitHandler = (event) => {
    event.preventDefault();
    const url = `https://youtube.googleapis.com/youtube/v3/search?part=snippet&maxResults=25&q=${searchTitle}&key=${APIkey}`;
    searchVideos(url);
  };

  return (
    <div className="search-bar">
      <form onSubmit={onSubmitHandler} className="search-form-controls">
        <input
          className="search-input"
          type="text"
          onChange={onChangeHandler}
          placeholder="Search Videos"
        />
        <button className="search-btn">Search</button>
      </form>
    </div>
  );
}
