import React, { useState } from "react";
import ReactPlayer from "react-player/youtube";
import "./App.css";
import Header from "./components/Header";
import SearchBar from "./components/SearchBar";
import VideoList from "./components/VideoList";
import videoAPI from "./api/VideoAPI";

function App() {
  const [video, setVideo] = useState(videoAPI[0]);
  const [searchTitle, setSearchTitle] = useState("");

  const onSubmitHandler = (title) => {
    setSearchTitle(title);
  };
  console.log(searchTitle);
  const clickTitleHandler = (video) => {
    setVideo(video);
  };
  return (
    <div className="App">
      <Header />
      <SearchBar onSubmit={onSubmitHandler} />

      <div className="content">
        <div className="searched-videos">
          <VideoList videos={videoAPI} onClickTitle={clickTitleHandler} />
        </div>
        <div className="searched-video">
          <ReactPlayer
            className="video-player"
            width="100%"
            height="62rem"
            controls
            pip={true}
            playing={true}
            url={video.url}
          />
          <h3 className="video-title">{video.title}</h3>
        </div>
      </div>
    </div>
  );
}

export default App;
