import React, { useState, useEffect } from "react";
import "./SearchBar.css";

// Search url=https://youtube.googleapis.com/youtube/v3/search?part=snippet&maxResults=25&q=ramayan&key=[YOUR_API_KEY]
const APIkey = "AIzaSyAtTWostq1MsBKVBHh6HeQ2bX6y6xj8YyE";

export default function SearchBar(props) {
  const [searchTitle, setSearchTitle] = useState("");

  const onChangeHandler = (event) => {
    setSearchTitle(event.target.value);
  };

  const onSubmitHandler = (event) => {
    event.preventDefault();
    const url = `https://youtube.googleapis.com/youtube/v3/search?part=snippet&maxResults=25&q=${searchTitle}&key=${APIkey}`;
    useEffect(() => {
      fetch(url);
    }, []);
  };

  return (
    <div className="search-bar">
      <form onSubmit={onSubmitHandler} className="search-form-controls">
        <input
          className="search-input"
          type="text"
          onChange={onChangeHandler}
          placeholder="Search Videos"
        />
        <button className="search-btn">Search</button>
      </form>
    </div>
  );
}
