import React, { useState } from "react";
import ReactPlayer from "react-player/youtube";
import "./App.css";
import Header from "./components/Header";
import SearchBar from "./components/SearchBar";
import VideoList from "./components/VideoList";
import videoAPI from "./api/VideoAPI";

const APIkey = "AIzaSyAtTWostq1MsBKVBHh6HeQ2bX6y6xj8YyE";
const playlistId = "PLjXBz6brb9YhM-UqVnm88WFNN5RiakvLo";
const url = `https://youtube.googleapis.com/youtube/v3/playlistItems?part=snippet%2CcontentDetails&maxResults=40&playlistId=${playlistId}&key=${APIkey}`;
function App() {
  const [results, setResults] = useState();
  const [video, setVideo] = useState({});
  const [searchTitle, setSearchTitle] = useState("");

  const onSubmitHandler = (title) => {
    setSearchTitle(title);
  };
  console.log(searchTitle);
  const clickTitleHandler = (video) => {
    setVideo(video);
  };

  const fetchVideoHandler = () => {
    fetch(url)
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        const videos = data.items.map((item) => {
          return {
            id: item.id,
            title: item.snippet.title,
            vid: item.contentDetails.videoId,
          };
        });
        setResults(videos);
      });
  };

  return (
    <div className="App">
      <Header />
      <SearchBar onSubmit={onSubmitHandler} />

      <button onClick={fetchVideoHandler}>fetch videos</button>
      {results && (
        <div className="content">
          <div className="searched-videos">
            <VideoList videoAPI={results} onClickTitle={clickTitleHandler} />
          </div>

          <ReactPlayer
            width="100%"
            height="60rem"
            controls
            playing
            url={`https://youtu.be/${video.vid}`}
            className="searched-video"
          />
        </div>
      )}
    </div>
  );
}

export default App;
