import React, { useState, useEffect } from "react";
import ReactPlayer from "react-player/youtube";

import "./App.css";
import Header from "./components/Header";
import SearchBar from "./components/SearchBar";
import VideoList from "./components/VideoList";

const APIkey = "AIzaSyAtTWostq1MsBKVBHh6HeQ2bX6y6xj8YyE";
const playlistId = "PLjXBz6brb9YhM-UqVnm88WFNN5RiakvLo";
const URL = `https://youtube.googleapis.com/youtube/v3/playlistItems?part=snippet%2CcontentDetails&maxResults=40&playlistId=${playlistId}&key=${APIkey}`;
function App() {
  const [results, setResults] = useState();
  const [video, setVideo] = useState({});

  const clickTitleHandler = (video) => {
    setVideo(video);
  };

  const searchHandler = (searchedVideos) => {
    setResults(searchedVideos);
    console.log(video);
  };

  const fetchVideoHandler = () => {
    fetch(URL)
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        const videos = data.items.map((item) => {
          return {
            id: Math.random().toString(),
            title: item.snippet.title,
            vid: item.contentDetails.videoId,
            publishedAt: item.snippet.publishedAt,
            description: item.snippet.description,
            channelTitle: item.snippet.channelTitle,
          };
        });
        console.log(videos);
        setResults(videos);
        setVideo(videos[0]);
      });
  };

  useEffect(() => {
    fetchVideoHandler();
  }, []);

  return (
    <div className="App">
      <Header />
      <SearchBar onSearch={searchHandler} />

      {results && (
        <div className="content">
          <div className="searched-videos">
            <VideoList videoAPI={results} onClickTitle={clickTitleHandler} />
          </div>
          <div className="searched-video">
            <ReactPlayer
              width="100%"
              height="60rem"
              controls
              playing
              url={`https://youtu.be/${video.vid}`}
              className="video-player"
            />
            <h3 className="video-title">{video.title}</h3>
            <p className="date">
              <span>Published At -</span>- {video.publishedAt}
            </p>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
